
public class StringDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           String s="abc";
           System.out.println(s);
           System.out.println(s.hashCode());
           
           s="pqr";
           System.out.println(s);
           System.out.println(s.hashCode());
	       
	       // String class is without a new keyword
	       // String is immutable
           // String is not changeable
	}

}
